mapper.update:
update orders
status = #{status}, rejection_reason = #{rejectionReason}, cancel_reason= #{cancelReason},
where id = #{id}

mapper.select:
select * from orders


        and number like concat('%',#{number},'%')


        and phone like concat('%',#{phone},'%')


        and user_id = #{userId}


        and status = #{status}


        and order_time >= #{beginTime}


        and order_time <= #{endTime}


order by order_time desc

select * from orders


        and number like concat('%',#{number},'%')


        and phone like concat('%',#{phone},'%')


        and user_id = #{userId}


        and status = #{status}


        and order_time >= #{beginTime}


        and order_time <= #{endTime}


order by order_time desc

select sum(amount)
from orders


        and order_time > #{begin}


        and order_time < #{end}


        and status = #{status}

select count(id) from user


        and create_time > #{begin}


        and create_time < #{end}

select count(id) from orders


        and order_time > #{begin}


        and order_time < #{end}


        and status = #{status}

SELECT name, SUM(order_detail.number) as number FROM order_detail, orders where order_detail.order_id = orders.id and orders.status = 5
and order_time > #{begin}
and order_time < #{end}
GROUP BY order_detail.name ORDER BY number DESC LIMIT 10
